const Evento = (props) => {
    const {evento} = props;
    let desc = evento.description.text;

    if(desc){
        desc = desc.substr(0, 250) + '...';
    }

    return(
        <a href={evento.url} target="_blank" className="btn btn-link mb-1">
            <h3 className="mb-3">{evento.name.text}</h3>
            <p className="mb-1">{desc}</p>
        </a>
    );
}

export default Evento;
