const Evento = (props) => {
    const {evento} = props;
    let desc = evento.description.text;

    if(desc){
        desc = desc.substr(0, 250) + '...';
    }

    return(
        
        <div class="card mb-3">
            <h3 class="card-header">Eventos en Mexico</h3>
            <div class="card-body">
                <h5 class="card-title">{evento.name.text}</h5>
            </div>
            {/* <img src="" alt="Card image" /> */}
            <div class="card-body">
                <p class="card-text">{desc}</p>
                <a href={evento.url} class="card-link">Ver Evento</a>
            </div>
        </div>
    );
}

export default Evento;
