const Noticia = (props) => {

    const { urlToImage, url, title, description, source } = props.noticia

    let imagen;

    if(urlToImage !== ''){
        image = <img src={urltoImage} alt={title} className="card-img-top" />
    }

    return(
        <div className="col-md-6 col-12 mb-4">
            <div className="card">
                <div className="card-image">
                    {image}
                </div>
            </div>
        </div>
    );
}

export default Noticia;
