import MasterPage from '../components/Master';
import fetch from 'isomorphic-unfetch';

const Index = (props) => (
    <MasterPage>
        <div className="row">
            <div className="col-12">
                <h2>Precio del bitcoin</h2>
                {console.log(props)}
            </div>
            <div className="col-md-8">
                <h2>Noticias de bitcoin</h2>
            </div>
            <div className="col-md-4">
                <h2>Eventos de bitcoin</h2>
            </div>
        </div>
    </MasterPage>
    
);

Index.getInitialProps = async () => {
    const key = '2bbeec3a-60af-494c-8954-f75bfacbbf27';
    const url = `https://pro-api.coinmarketcap.com/v1/cryptocurrency/quotes/latest?slug=bitcoin&convert=USD&CMC_PRO_API_KEY=${key}`;
    const precio = await fetch(url);
    const resPrecio = await precio.json();
    const json = {}
    for( const currency of resPrecio.data){
        json = currency.quote
    }
    return {
        precioBitcoin: json
    }
}

export default Index;
