import Link from 'next/link';

const Navegacion = () => (
    <div>
        <ul>
            <li>
                <Link href="/"><a>Inicio</a></Link>
            </li>
        </ul>
        {/* Este estilo solo afecta a este componente */}
        <style jsx>{`
            ul{
                background-color: #333;
                list-style:none;
                display:flex;
            }
            ul li{
                padding: 0.5 rem 0;
                margin-right:1rem;
            }
            ul li a{
                font-size:1.2rem;
                color: white;
                text-decoration: none;
            }
        `}</style>
    </div>
);

export default Navegacion;
